//  abstract syntax tree generated by yacc grammar
package main

import (
	"errors"
	"fmt"
)

type ast struct {

	yy_tok		int
	line_no		int

	//  children
	left		*ast
	right		*ast

	//  siblings
	next		*ast
	prev		*ast

	parent		*ast

	//  golang needs unions!

	tracer_ref	*tracer
	scanner_ref	*scanner
	command_ref	*command
	uint64
	string
	array_ref	[]string
}

func (a *ast) name() string {
	if a == nil {
		return "nil"
	}
	return yy_name(a.yy_tok)
}

func (a *ast) String() string {

	var what string

	if a == nil {
		return "nil AST"
	}

	switch a.yy_tok {
	case 0:
		a.corrupt("ast has yy_tok == 0")
	case ARG_LIST:
		what = fmt.Sprintf("ARG_LIST(argc=%d)", a.uint64)
	case SCANNER_REF:
		what = fmt.Sprintf("SCANNER_REF(%s)", a.scanner_ref.name)
	case COMMAND_REF:
		what = fmt.Sprintf("COMMAND_REF(%s)", a.command_ref.name)
	case TRACER_REF:
		what = fmt.Sprintf("TRACER_REF(%s)", a.tracer_ref.name)
	case STRING:
		what = fmt.Sprintf("STRING(%s)", a.string)
	case NAME:
		what = fmt.Sprintf("NAME(%s)", a.string)
	case UINT64:
		what = fmt.Sprintf("UINT64(%d)", a.uint64)
	case ATT_ARRAY:
		ar := a.array_ref
		what = fmt.Sprintf("ATT_ARRAY(len=%d,cap=%d)", len(ar), cap(ar))
	case yy_AND:
		what = "AND"
	case yy_OR:
		what = "OR"
	case yy_FALSE:
		what = "FALSE"
	case yy_TRUE:
		what = "TRUE"
	case STMT_LIST:
		what = fmt.Sprintf("STMT_LIST(cnt=#%d)", a.uint64)
	case STMT:
		what = fmt.Sprintf(
				"STMT(ord=#%d@lno=#%d)",
				a.uint64,
				a.line_no,
		)
	default:
		what = a.name()
	}
	return what
}

func (a *ast) walk_print(indent int, parent *ast) {

	if a == nil {
		return
	}
	if a.parent != parent {
		if a.parent == nil {
			a.corrupt("unexpected nil parent")
		} else {
			a.corrupt("unexpected parent: %s", a.parent.name())
		}
	}
	if indent == 0 {
		fmt.Println("")
	} else {
		if a.parent == nil {
			corrupt("parent ast is nil")
		}
		for i := 0;  i < indent;  i++ {
			fmt.Print("  ")
		}
	}
	fmt.Println(a.String())

	//  print kids

	a.left.walk_print(indent + 1, a)
	a.right.walk_print(indent + 1, a)

	//  print siblings

	if a.prev == nil {
		for as := a.next;  as != nil;  as = as.next {
			as.walk_print(indent, parent)
		}
	}
}

func (a *ast) print() {
	a.walk_print(0, nil)
}

func (a *ast) frisk_att(what string, need...ast) error {

	if a.yy_tok != ATT_TUPLE {
		corrupt("start node not ATT_TUPLE")
	}

	const fmt_dup = "duplicate attribute"
	const fmt_need = "need attribute"
	const fmt_no = "unknown attribute"
	const fmt_type = "wrong type"
	const fmt_nostr = "STRING empty"

	err := func(name string, lno int, msg string) error {

		name = "node \"" + name + "\""
		near := ", near line " + fmt.Sprintf("%d", lno)
		return errors.New(msg + ": " + what + ": " + name + near)
	}

	//  map list of need ast to name for quick check

	name2need := make(map[string]*ast)
	for _, an := range need {
		name2need[an.string] = &an
	}

	seen := make(map[string]*ast)

	//  scan each ATT nodes of ATT_TUPLE

	for an := a.left;  an != nil;  an = an.next {
		if an.yy_tok != ATT {
			an.corrupt("left node not ATT")
		}
		name := an.left.string
		
		//  more than one att exists
		if seen[name] != nil {
			return err(name, seen[name].line_no, fmt_dup)
		}
		ar := an.right
		if ar == nil {
			an.corrupt("ATT has nil right")
		}

		//  is the node known?
		ann := name2need[name]
		if ann == nil {
			return err(name, an.line_no, fmt_no)	//  unkn
		}

		/*
		 * do the types match?
		 *
		 *  Note:
		 *	error message "wrong type:" is opaque.  ought to include
		 *	expected type
		 */
		if ar.yy_tok != ann.yy_tok {
			return err(name, ar.line_no, fmt_type)
		}

		//  no empty strings
		if ar.yy_tok == STRING && ar.string == "" {
			return err(name, ar.line_no, fmt_nostr)
		}
		seen[name] = ar
	}

	//  scan for required nodes in "need" twig
	for nm, an := range name2need {
		
		if an.uint64 > 0 && seen[nm] == nil {
			return err(nm, an.line_no, fmt_need)
		}
	}

	return nil
}

func (a *ast) corrupt(format string, args...interface{}) {

	msg := fmt.Sprintf(format, args...)
	corrupt("%s: node \"%s\", near line %d", msg, a.name(), a.line_no)
	//  NOTREACHED*/
}

func (at *ast) find_ATT(name string) (*ast) {

	if at.yy_tok != ATT_TUPLE {
		at.corrupt("node not ATT_TUPLE")
	}
	for a := at.left;  a != nil;  a = a.next {
		if a.parent != at {
			a.corrupt("parent not ATT_TUPLE")
		}
		if a.yy_tok != ATT {
			a.corrupt("child not ATT")
		}
		if a.left == nil {
			a.corrupt("left is nil")
		}
		if a.left.string == name {
			return a
		}
	}
	return nil
}

//  is the node a binary operator(), left and right must exit
func (a *ast) is_binary() bool {
	switch a.yy_tok {
	case yy_OR, yy_AND, LT, LTE, EQ, NEQ, GTE, GT:
		return true
	}
	return false
}

//  is the node a unary operator(), left only exists
func (a *ast) is_unary() bool {
	switch a.yy_tok {
	case NOT, WHEN:
		return true
	}
	return false
}

func (a *ast) is_flowable() bool {
	switch a.yy_tok {
	case RUN:
		return true
	}
	return false
}

//  insure type of parent ast node is in expected yy token set...

func (a *ast) frisk_parent(expect ...int) error {
	if a == nil {
		return errors.New("frisk_parent: unexpected nil node")
	}

	if a.parent == nil {
		return errors.New("frisk_parent: parent is nil")
	}
	if !a.parent.in_tok_set(expect...) {
		return errors.New(fmt.Sprintf(
			"parent (%s) of %s not in yy token set: %s",
			a.parent.name(),
			a.name(),
			yy_names(expect...),
		))
	}
	return nil
}

func (a *ast) in_tok_set(expect ...int) bool {

	for _, tok := range expect {
		if tok == a.yy_tok {
			return true
		}
	}
	return false
}

func (a *ast) error(format string, args...interface{}) error {

	return errors.New(
		fmt.Sprintf("node %s: %s",
			a.name(),
			fmt.Sprintf(format, args...),
	))
}

//  verify type of ast kids are in expected yy type set... and
//  point to proper relatives.

func (a *ast) frisk_kids(twig string, expect ...int) error {
	if a == nil {
		return errors.New(twig + " node is kill")
	}

	e := func(kid *ast, format string, args...interface{}) error {
		fmt := fmt.Sprintf("%s kid (%s)", twig, kid.name())
		return a.error(fmt, args...)
	}

	var k_prev, k *ast
	if twig == "left" {
		k = a.left
	} else {
		k = a.right
	}
	if k == nil {
		return nil
	}
	for ;  k.next != nil;  k = k.next { 
		if k.parent == nil {
			return e(k, "has nil parent")
		}
		if k.parent != a {
			return e(k, "has wrong parent (%s)", k.parent.name()) 
		}
		if k_prev != nil && k.prev != k_prev {
			return e(k, "has wrong prev (%s)", k.prev.name())
		}
		found := k.in_tok_set(expect...)
		if !found {
			return e(k, "not in tok set (%s)", yy_names(expect...))
		}
		k_prev = k
	}
	return nil
}

//
//  derive the type of the ast expression.
//
//  Note:
//	why not put the type of an expression in the ast while parsing
//
func (a *ast) yy_type() string {
	switch a.yy_tok {
	case yy_TRUE, yy_FALSE,
	     LT,  LTE,  EQ,  NEQ,  GTE,  GT:
		return "bool"
	case STRING, CONCAT:
		return "string"
	case UINT64:
		return "uint64"
	}
	return "unknown"
}

//  Frisk an ast tree for wiring mistakes, panic on error

func (a *ast) frisk() {

	//  can we invoke a.frisk() on nil *ast?

	if a == nil {
		return
	}

	_corrupt := func(format string, args...interface{}) {
		a.corrupt("frisk: " + format, args...)
	}

	if a.left != nil {
		a.left.frisk()
	}
	if a.right != nil {
		a.right.frisk()
	}

	if a.is_binary() {
		if a.left == nil {
			_corrupt("left child is nil")
		}
		if a.right == nil {
			_corrupt("right child is nil")
		}
	} else if a.is_unary() {
		if a.right != nil {
			_corrupt("right exists for unary op")
		}
	}
	if a.parent == nil {
		_corrupt("parent is nil")
	}

	ckparent := func(expect ...int) {

		err := a.frisk_parent(expect...)
		if err != nil {
			_corrupt("ckparent: %s", err)
		}
	}

	ckleft := func(expect ...int) {

		if a.left == nil {
			_corrupt("left is nil")
		}
		err := a.frisk_kids("left", expect...)
		if err != nil {
			_corrupt("ckleft: %s", err)
		}
	}

	ckrelop := func() {

		if a.left == nil {
			_corrupt("ckrelop: left is nil")
		}

		tok := a.left.yy_tok
		switch tok {
		case STRING, UINT64, yy_TRUE, yy_FALSE:
		default:
			_corrupt("bad relop type: %s", yy_name(tok))
		}
	}

	switch a.yy_tok {
	case NAME:
	case ATT:
		ckparent(ATT_TUPLE)
	case ATT_TUPLE:
		ckparent(COMMAND_REF, SCANNER_REF, TRACER_REF)
	case yy_TRUE:
	case yy_FALSE:
	case STRING:
	case UINT64:
	case SCANNER_REF:
		ckleft(ATT_TUPLE)
	case CREATE:
		ckparent(STMT)
	case STMT:
		ckparent(STMT_LIST)
	case STMT_LIST:
		_corrupt("unexpected STMT_LIST")
	case TRACER_REF, COMMAND_REF:
		ckleft(ATT_TUPLE)
	case CONCAT:
	case ARG_LIST:
		if a.right != nil {
			_corrupt("right node (%s) is not nil", a.right.name())
		}

		//  verify all args are expressions with proper parent
		for arg := a.left;  arg != nil;  arg = arg.next {
			if arg.parent != a {
				fmt := "parent (%s) of arg (%s) not this arg"
				_corrupt(fmt, arg.parent.name(), arg.name())
			}
			if arg.is_expression() == false {
				_corrupt("arg node (%s) not expr", arg.name())
			}
		}
	case RUN:
		/*
		 *  Parse tree
	         *	RUN
		 *		ARG_LIST(argc=0)
		 *		WHEN
		 *			(bool expression)?
		 */
		al := a.left
		if al == nil {
			_corrupt("left node is nil")
		}
		if al.yy_tok != ARG_LIST {
			_corrupt("left node %s is not ARG_LIST", al.name())
		}

		w := a.right
		if w != nil {
			if w.yy_tok != WHEN {
				_corrupt("right node (%s) not WHEN", w.name())
			}
			wl := w.left
			if wl == nil {
				_corrupt("WHEN.left is nil")
			}
			if wl.is_bool() == false {
				_corrupt("WHEN.left (%s) not bool", wl.name())
			}
		}
	case LT, LTE, EQ, NEQ, GTE, GT:
		ckrelop()
	case yy_OR:
	case yy_AND:
	case NOT:
	case WHEN:
		if a.parent.is_flowable() == false {
			nm := a.parent.name()
			_corrupt("parent of WHEN (%s) not flowable", nm)
		}
		if a.left.is_bool() == false {
			_corrupt("qualification (%s) not bool", a.left.name())
		}
	default:
		_corrupt("unknown ast node")
	}
}

//  Note: outght too be "is_value()"
func (a *ast) is_expression() bool {
	return a.is_bool() || a.is_string() || a.is_uint64()
}
